<style>
	section {
		padding-block: 1.5rem 0;
		transition: all 0.5s;
		scrollbar-gutter: stable both-edges;
		overflow: hidden;
		margin-inline-start: -4px; /* account for the scrollbar area */
		mask-image: linear-gradient(
			to bottom,
			transparent 0%,
			white 2%,
			white 95%,
			white 95%,
			transparent 100%
		);
		mask-repeat: no-repeat;
		mask-size: 100% 100%;
		container: sidebar / inline-size;

		&:hover {
			overflow: auto;
		}
	}

	ul {
		padding-inline-end: 2rem;
		padding-block-end: 1rem;
		opacity: 0.6;
		transition: opacity 0.4s ease-out;

		&:hover {
			opacity: 1;
		}
	}

	li {
		line-height: 1.15;
		margin-block-end: 1rem;
		display: flex;
		align-items: center;
		gap: 1rem;

		opacity: 0.8;
		transition: opacity 0.2s ease-out;

		&:hover {
			opacity: 1;
		}
	}

	ul:has(a.active) {
		opacity: 1;
	}

	li:not(:has(a.active)) {
		opacity: 0.8;

		&:hover {
			opacity: 1;
		}
	}

	li:has(a.active) {
		opacity: 1;
	}

	a {
		position: relative;
		color: #fff;
		text-decoration: none;

		&::after {
			content: '';
			position: absolute;
			width: min-content;
			height: 100%;
			background: #fff;

			inset: 0;
			scale: 0 1;
			transition: scale 0.2s ease-out;
			transform-origin: left center;
		}

		&:hover {
			text-decoration: none;
		}

		&:hover::after {
			scale: 1;
		}
	}

	a.active {
		font-weight: 600;
	}

	svg {
		flex-shrink: 0;
		fill: #fff;
		/* stroke: 12px; */

		/* path {
			stroke-width: 2px;
		} */
	}

	@container sidebar (max-width: 238px) {
		calcite-icon,
		svg {
			display: none;
		}
	}
</style>
